mapper.select:
select articleno, userid, subject, content, regtime
from guestbook


		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by articleno desc
limit #{start}, #{spp}

select count(articleno) from guestbook
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, regtime
from guestbook
where articleno = #{articleno}

mapper.update:
update guestbook
set subject = #{subject}, content = #{content}
where articleno = #{articleno}
